     }

 })(batch, output);
 */

 (function(batch, output) {
	for (var item in batch)

	{
		var type=batch[item].u_type
		switch(type){
			case 'Windows PC': output[item].u_type='cmdb_ci_computer';break;
			case 'Windows Server': output[item].u_type='cmdb_ci_win_server';break;
			case 'Linux Server': output[item].u_type='cmdb_ci_linux_server';break;
			case 'Linux GUI': output[item].u_type='cmdb_ci_computer';break;
			case 'ESXi Host': output[item].u_type='cmbd_ci_esx_server';break;
		}
	}
  // add your code here.
 })(batch, output);
